import React, { useState } from "react"; 
import { post } from 'axios'; 

function CreateRecipe(props) {
  const initialState = { title: '', content: '' }
  const [recipe, setRecipe] = useState(initialState) 

  function handleChange(event) { 
    setRecipe({...recipe, [event.target.name]: event.target.value})
  }

  function handleSubmit(event) { 
    event.preventDefault();  
    if(!recipe.title || !recipe.content ) return 
    async function postRecipe() {
      try {
        const response = await post('http://localhost:3001/api/recipes', recipe); 
        props.history.push(`http://localhost:3001/api/recipes/${response.data._id}`);  
      } catch(error) {
        console.log('error', error);
      }
    }
    postRecipe();
  }

  function handleCancel() {
    props.history.push("http://localhost:3001/api/recipes");
  }

  return ( 
    <div>
      <h1>Create recipe</h1>
      <hr/>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input name="title" type="text" value={recipe.title} onChange={handleChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Content</label>
          <textarea name="content" rows="5" value={recipe.content} onChange={handleChange} className="form-control" />
        </div>
        <div className="btn-group">
          <input type="submit" value="Submit" className="btn btn-primary" />
          <button type="button" onClick={handleCancel} className="btn btn-secondary">Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default CreateRecipe;
