import React, { useState, useEffect } from "react";
import { get, patch } from 'axios';

function EditRecipe(props) {

  const initialState = { title: '', content: '' }
  const [recipe, setRecipe] = useState(initialState)

  useEffect(function() {
    async function getRecipe() {
      try {
        const response = await get(`/api/recipes/${props.match.params._id}`);
        setRecipe(response.data);        
      } catch(error) {
        console.log(error);
      }
    }
    getRecipe();    
  }, [props]);

  function handleSubmit(event) {
    event.preventDefault();
    async function updateRecipe() {
      try {
        await patch(`/api/recipes/${recipe.id}`, recipe);
        props.history.push(`/recipes/${recipe.id}`);        
      } catch(error) {
        console.log(error);
      }
    }
    updateRecipe();
  }

  function handleChange(event) {
    setRecipe({...recipe, [event.target.name]: event.target.value})
  }

  function handleCancel() {
    props.history.push(`/recipes/${recipe.id}`);
  }

  return (
    <div>
      <h1>Edit {recipe.title}</h1>
      <hr/>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input type="text" name="title" value={recipe.title} onChange={handleChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Content</label>
          <textarea name="content" rows="5" value={recipe.content} onChange={handleChange} className="form-control" />
        </div>
        <div className="btn-group">
          <button type="submit" className="btn btn-primary">Update</button>
          <button type="button" onClick={handleCancel} className="btn btn-secondary">Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default EditRecipe;
