import React, { useState, useEffect } from "react";
import axios from 'axios'; 
import { Link } from 'react-router-dom';
import './RecipeInfo.css'

export default function RecipeInfo(props) {
  const [recipe, setrecipe] = useState({}); 

  useEffect(function() { 
    async function getRecipe() {
      try {
        const response = await axios.get(`http://localhost:3001/api/recipes/${props.match.params.id}`); 
        setrecipe(response.data);      
      } catch(error) {
        console.log('error', error);
      }
    }
    getRecipe();    
  }, [props]); 

  async function handleDelete() { 
    try {
      await axios.delete(`http://localhost:3001/api/recipes/${props.match.params.id}`); 
      props.history.push("http://localhost:3001/recipes"); 
    } catch(error) {
      console.error(error);
    }
  }

  return ( 
    <div className="RecipeInfo">
      <section className="recipeInfoContainer">
        <div className="imageSummary">
          <img src="http://placekitten.com/300/400" alt="cute kitty!"/>
          <div className="summary mdc-card">
            <p>Recipe summary here</p>
          </div>
        </div>
      <div className="ingInstContainer">
        <div className="ingredients mdc-card">
          <h2>{recipe.title}</h2>
          <p>Ingredients list here</p>
        </div>
        <div className="instructions mdc-card">
          <h3>Instructions</h3>
          <p>{recipe.content}</p>
        </div>
      </div> 
      </section>

      <div className="btnContainer">
        <Link to={`/recipes/${recipe._id}/edit`} className="mdc-button mdc-button--raised" id="edit">Idiot Sandwich</Link> 
        <button onClick={handleDelete} className="mdc-button mdc-button--raised" id="delete" >Shut it down!!</button> 
        <Link to="/recipes" className="mdc-button mdc-button--raised" id="close">**** Off</Link>
      </div>
    </div>
  );
};
