import React, { useState, useEffect } from "react";
import axios from 'axios'; 
import { Link } from 'react-router-dom';

export default function RecipeInfo(props) {
  const [recipe, setrecipe] = useState({}); 

  useEffect(function() { 
    async function getRecipe() {
      try {
        const response = await axios.get(`/api/recipes/${props.match.params._id}`); 
        setrecipe(response.data);      
      } catch(error) {
        console.log('error', error);
      }
    }
    getRecipe();    
  }, [props]); 

  async function handleDelete() { 
    try {
      await axios.delete(`/api/recipes/${props.match.params._id}`); 
      props.history.push("/recipes"); 
    } catch(error) {
      console.error(error);
    }
  }

  return ( 
    <div>
      <h2>{recipe.title}</h2>
      <small>_id: {recipe._id}</small>
      <p>{recipe.content}</p>
      <div className="btn-group">
        <Link to={`/recipes/${recipe._id}/edit`} className="btn btn-primary">Edit</Link> 
        <button onClick={handleDelete} className="btn btn-danger">Delete</button> 
        <Link to="/recipes" className="btn btn-secondary">Close</Link>
      </div>
      <hr/>
    </div>
  );
};
